library rsaverify {
    // This copies call data (everything, except the method signature) to the precompiled contract.
    function verify(bytes msg, bytes N, uint e, bytes S, uint paddingScheme) public returns (bool) {
        uint len;
        assembly {
            len := calldatasize()
        }

        bytes memory req = new bytes(len - 4);
        bytes memory res = new bytes(32);

        uint status;

        assembly {
            let alen := len
            calldatacopy(req, 4, alen)
            call(sub(gas, 150), 0x05, 0, req, alen, add(res, 32), 32)
            =: status
        }

        if (status != 1)
            return false;

        return res[31] == 1;
    }

